mapper.select:
select aa.JID as jid, aa.JGoodsCode as jgoodscode, aa.JGoodsName as jgoodsname, aa.JModel as jmodel, aa.JUnit as junit, aa.JMemo as jmemo, aa.JClassID as jclassid, aa.JBarCode as jbarcode, convert(decimal(10, 2),aa.JPointSalePrice) as jpointsaleprice, convert(decimal(10, 2),aa.JSale3Price) as jsale3price, convert(decimal(10, 2),aa.JRefCostPrice) as jrefcostprice, convert(decimal(10, 2),aa.JMinInPrice) as jmininprice, aa.JPlace as jplace,
isnull(aa.JSubtitle,'') as jsubtitle, isnull(aa.JBigPhoto,'') AS jbigphoto, isnull(aa.JLitPhoto,'') as jlitphoto, isnull(bb.JSupClientName,'') as jsupclientname, isnull(aa.JPhotoScript,'') as jphotoscript
FROM dbo.TGoods aa,dbo.TSupClient bb where aa.JSupClientID = bb.JID and aa.JID = #{jid,jdbcType=INTEGER}
